import { useMeasure } from '@uidotdev/usehooks'
import { cva, type VariantProps } from 'class-variance-authority'
import * as React from 'react'

import { cn } from '@/lib/utils'

const inputVariants = cva(
  cn(
    'relative flex w-full min-w-0 items-center overflow-hidden rounded-md border border-input bg-background/80 text-base text-foreground shadow-xs transition-[color,box-shadow] md:text-sm',
    'selection:bg-primary selection:text-primary-foreground',
    '[&>input]:placeholder:text-muted-foreground',
    'focus-within:border-ring focus-within:ring-[3px] focus-within:ring-ring/50',
    'has-[[aria-invalid]]:border-destructive has-[[aria-invalid]]:ring-destructive/20 dark:has-[[aria-invalid]]:ring-destructive/40',
    'has-disabled:opacity-50'
  ),
  {
    variants: {
      size: {
        default: 'h-9 [&>div]:px-2.5 [&>input]:px-3',
        sm: 'h-8 [&>div]:px-2 [&>input]:px-2.5',
        lg: 'h-10 md:text-base [&>div]:px-3 [&>input]:px-4',
      },
    },
    defaultVariants: {
      size: 'default',
    },
  }
)

type InputProps = Pick<
  React.ComponentProps<'input'>,
  | 'type'
  | 'className'
  | 'placeholder'
  | 'id'
  | 'value'
  | 'defaultValue'
  | 'disabled'
  | 'readOnly'
  | 'aria-invalid'
  | 'aria-describedby'
  | 'autoFocus'
  | 'autoCapitalize'
  | 'autoComplete'
  | 'autoCorrect'
  | 'onBlur'
  | 'onChange'
> &
  VariantProps<typeof inputVariants> & {
    startElement?: React.ReactNode
    endElement?: React.ReactNode
    inputClassName?: string
  }

const startEndElementClassNames = (className?: string) =>
  cn(
    'absolute top-0 bottom-0 flex items-center justify-center text-muted-foreground',
    'pointer-events-none *:not-[svg]:pointer-events-auto',
    "[&_svg]:shrink-0 [&_svg:not([class*='size-'])]:size-[1em]",
    className
  )

function Input({
  className,
  inputClassName,
  type,
  startElement,
  endElement,
  size,
  ...props
}: InputProps) {
  const [startElementRef, { width: startElementWidth }] = useMeasure()
  const [endElementRef, { width: endElementWidth }] = useMeasure()
  return (
    <div className={cn(inputVariants({ size }), className)}>
      {!!startElement && (
        <div
          ref={startElementRef}
          className={startEndElementClassNames('left-0')}
        >
          {startElement}
        </div>
      )}
      <input
        {...props}
        type={type}
        data-slot="input"
        className={cn(
          'flex h-full w-full outline-none',
          'disabled:cursor-not-allowed, disabled:bg-muted',
          inputClassName
        )}
        style={{
          paddingLeft: startElementWidth ?? undefined,
          paddingRight: endElementWidth ?? undefined,
        }}
      />
      {!!endElement && (
        <div
          ref={endElementRef}
          className={startEndElementClassNames('right-0')}
        >
          {endElement}
        </div>
      )}
    </div>
  )
}

export { Input }

export type { InputProps }

import type { Meta } from '@storybook/react'
import { MailIcon } from 'lucide-react'

import { Button } from '@/registry/ui/button'
import { Input } from '@/registry/ui/input'

export default {
  title: 'Components/Input',
} satisfies Meta<typeof Input>

export const Default = () => {
  return <Input placeholder="Placeholder..." />
}

export const Invalid = () => {
  return <Input aria-invalid placeholder="Placeholder..." />
}

export const Disabled = () => {
  return <Input disabled placeholder="Placeholder..." />
}

export const Sizes = () => {
  return (
    <div className="flex flex-col gap-4">
      <Input size="sm" placeholder="Placeholder..." />
      <Input placeholder="Placeholder..." />
      <Input size="lg" placeholder="Placeholder..." />
    </div>
  )
}

export const StartEndElements = () => {
  return (
    <div className="flex flex-col gap-4">
      <Input
        startElement={<MailIcon />}
        defaultValue="Icon start"
        placeholder="Placeholder..."
      />
      <Input
        endElement={<MailIcon />}
        defaultValue="Icon end"
        placeholder="Placeholder..."
      />
      <Input
        startElement={<MailIcon />}
        endElement={<MailIcon />}
        defaultValue="Icon start and end"
        placeholder="Placeholder..."
      />
      <Input
        startElement="https://"
        defaultValue="Text start"
        placeholder="Placeholder..."
      />
      <Input
        endElement=".com"
        defaultValue="Text end"
        placeholder="Placeholder..."
      />
    </div>
  )
}

export const InputAndButton = () => {
  return (
    <div className="flex flex-col gap-4">
      <form
        className="flex gap-2"
        onSubmit={(e) => {
          e.preventDefault()
          alert('submitted')
        }}
      >
        <Input placeholder="Placeholder..." />
        <Button type="submit">Submit</Button>
      </form>
      <form
        onSubmit={(e) => {
          e.preventDefault()
          alert('submitted')
        }}
      >
        <Input
          className="pr-1"
          placeholder="Placeholder..."
          endElement={
            <Button type="submit" size="xs" className="-mr-1.5">
              Submit
            </Button>
          }
        />
      </form>
      <form
        className="flex gap-2"
        onSubmit={(e) => {
          e.preventDefault()
          alert('submitted')
        }}
      >
        <Input size="sm" placeholder="Placeholder..." />
        <Button size="sm" type="submit">
          Submit
        </Button>
      </form>
      <form
        className="flex gap-2"
        onSubmit={(e) => {
          e.preventDefault()
          alert('submitted')
        }}
      >
        <Input size="lg" placeholder="Placeholder..." />
        <Button size="lg" type="submit">
          Submit
        </Button>
      </form>
      <form
        onSubmit={(e) => {
          e.preventDefault()
          alert('submitted')
        }}
      >
        <Input
          className="pr-1"
          placeholder="Placeholder..."
          size="lg"
          endElement={
            <Button type="submit" size="sm" className="-mr-2">
              Submit
            </Button>
          }
        />
      </form>
    </div>
  )
}
